import React, { Component, Fragment } from 'react'
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider'
import AppBar from 'material-ui/AppBar'
import TextField from 'material-ui/TextField'
import RaisedButton from 'material-ui/RaisedButton'


export class DetailEngineer extends Component {
	continue = e => {
		e.preventDefault();
		this.props.nextStep()
	}

	back = e => {
		e.preventDefault();
		this.props.prevStep()
	}

    render() {
    	const { values, handleChange } = this.props;
        return (

            <MuiThemeProvider>
                <Fragment>
                	<AppBar title="Enter Personal Detail" />
                	<TextField
                		hintText="Enter your full name"
                		floatingLabelText="Name"
                		onChange={handleChange('name_engineer')}
                		defaultValue={values.name_engineer}	
                	/>
                	<TextField
                		hintText="Enter your description"
                		floatingLabelText="Description"
                		onChange={handleChange('description')}
                		defaultValue={values.description}	
                	/>
                	<TextField
                		hintText="Enter your location"
                		floatingLabelText="Location"
                		onChange={handleChange('location')}
                		defaultValue={values.location}	
                	/>
                	<TextField
                		hintText="Enter your DOB"
                		floatingLabelText="Date of birth"
                		onChange={handleChange('birth')}
                		defaultValue={values.birth}	
                	/>
                	<RaisedButton
                		label="Submit"
                		primary={true}
                		style={styles.button}
                		onClick={this.continue}
                	/>
                	<RaisedButton
                		label="Back"
                		primary={false}
                		style={styles.button}
                		onClick={this.back}
                	/>



                </Fragment>
            </MuiThemeProvider>
        )
    }
}

const styles = {
	button: {
		margin: 15
	}
}


export default DetailEngineer
