import React, { Component, Fragment } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import TypoGraphy from '@material-ui/core/Typography';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import SmsRoundedIcon from '@material-ui/icons/SmsRounded';
import NotificationsIcon from '@material-ui/icons/Notifications';
import logo from '../image/arkademy-logo.png';
import CardList from './CardList';
import getAuth from '../helpers/auth';

import '../styles/Navbar.css';


class Navbar extends Component {

  handleLogout(e) {
    const auth = getAuth()
    const clear = localStorage.clear(this.setState.auth);
    console.log("CLEAR ", clear)
    // localStorage.setItem('token', this.state.token)
    // localStorage.setItem('id_user', this.state.id_user)
    // localStorage.setItem('username', this.state.username)
    // localStorage.setItem('role', this.state.role)
  }
    render() {
      return (
        <Fragment>
          <AppBar position="fixed" color="inherit">
            <Toolbar>
              <div className="nav-group">
                <img src={logo} className="logo" alt="logo" />
                <div className="search">
                  <SearchIcon />
                  <InputBase
                    placeholder="Searchâ€¦"
                    inputProps={{ 'aria-label': 'search' }}
                  />
                </div>
                <div className="home">
                  <TypoGraphy variant="inherit" gutterbottom="true">
                    <h4>Home</h4>
                  </TypoGraphy>
                  <TypoGraphy className="user-detail" variant="inherit" gutterbottom="true">
                      <Avatar>U</Avatar>
                      <h4>User</h4>
                  </TypoGraphy>
                  <TypoGraphy className="typo-wrap" variant="inherit" gutterbottom="true">
                      <hr width="1" size="40" />
                      <SmsRoundedIcon className="chat"/>
                      <NotificationsIcon className="notif"/>
                  </TypoGraphy>
                  <Button onClick={(e) => this.handleLogout()}>Logout</Button>
                </div>
              </div>
            </Toolbar>
          </AppBar>
          <CardList />
        </Fragment>
      )
    }
  }

export default Navbar
