import React, { Component } from 'react';
import { Grid, TextField, Button } from '@material-ui/core/'
// import AppBar from '@material-ui/core/AppBar';
// import Toolbar from '@material-ui/core/Toolbar';
// import TypoGraphy from '@material-ui/core/Typography';
import logo from '../image/Arkademy-Putih.svg';
import hiring from '../image/vector-hiring.png';
import './SignIn.css';

class SignIn extends Component {
    render() {
      return (
        <Grid container sm={12}>
          <Grid item sm={7}>
            <div className="left-side">
              <img src={logo} className="logo-white" alt="logo"></img>
              <img src={hiring} className="logo-hiring" alt="hiring"></img>
              <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Ullam odio repudiandae iste. Beatae, dignissimos saepe expedita sit</p>
            </div>
          </Grid>
          <Grid item sm={5}>
            <h1>Login</h1>
            <div className="login-group">
              <div className="field-username">
                {/* <TextField id="username" label="Enter your username.." /> */}
                <TextField
                id="standard-full-width"
                label="Username"
                style={{ margin: 8 }}
                placeholder="Input username..."
                // helperText="Full width!"
                fullWidth
                margin="normal"
                InputLabelProps={{
                  shrink: true,
                }}
              />
              </div>
              <div className="field-password">
                <TextField id="password" label="Enter your password.." />
              </div>
              <div className="button-login">
                <Button variant="contained" color="primary">
                  Login
                </Button>
              </div>
              <div className="button-register">
                <Button variant="contained" color="second">
                  Register
                </Button>
              </div>
            </div>
          </Grid>
        </Grid>
      )
    }
  }

export default SignIn
