import React, { Component } from 'react';
import { Grid, TextField, Button } from '@material-ui/core/';
import { Link } from 'react-router-dom';
import axios from 'axios';
import logo from '../image/Arkademy-Putih.svg';
import hiring from '../image/vector-hiring.png';
import './Register.css';

class Register extends Component {
  constructor(){
    super();

    this.state = {
      username: '',
      password: '',
      role:''
    }

    this.username = this.username.bind(this);
    this.password = this.password.bind(this);
    this.role = this.role.bind(this);
  }

    username = (e) => {
      console.log("value ",e.target.value)
      this.setState({username: e.target.value})
    }
    password = (e) => {
      console.log("value ",e.target.value)
      this.setState({password: e.target.value})
    }
    role = (e) => {
      console.log("value ",e.target.value)
      this.setState({role: e.target.value})
    }

    handleRegister = (e) => {
      axios.post('http://localhost:5000/register', {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          username: this.state.username,
          password: this.state.password,
          role: this.state.role
        })
      })
      .then((Response) => Response.json())
      .then((Result) => {
        if (Result.Status == 'Success')
          this.props.history.push("/card");
        else
          alert('Sorry! Un-auth user.')
      })
    }


    render() {
      return (
        <Grid container sm={12}>
          <Grid item sm={7}>
            <div className="left-side">
              <img src={logo} className="logo-white" alt="logo"></img>
              <img src={hiring} className="logo-hiring" alt="hiring"></img>
              <div className="wrap-caption">
                <h2><b>Hire expert freelancers for any jobs, online</b></h2>
                <p>Millions of small businesses use Frelancer to turn their ideas into reality.</p>
              </div>
            </div>
          </Grid>
          <Grid item sm={5}>
            <h1>Register</h1>
            <form method="post" type="submit">
              <div className="login-group">
                <div className="field-username">
                  <label>Username</label>
                  <TextField 
                  fullWidth 
                  id="username" 
                  label="Register your username.." 
                  value={this.state.username}
                  onChange={this.username}/>
                </div>
                <div className="field-password">
                  <label>Password</label>
                  <TextField 
                  fullWidth
                  id="password" 
                  label="Enter your password.." 
                  onChange={this.password}/>
                </div>
                <div className="field-role">
                  <label>Role</label>
                  <TextField 
                  fullWidth 
                  id="role" 
                  label="company / engineer" 
                  onChange={this.role}/>
                </div>
                <div className="button-login">
                  <Button 
                  variant="contained" 
                  color="primary"
                  onClick={this.handleRegister}>
                    Register
                  </Button>
                </div>

                <div className="have-account">
                  <Link to='/login'>Have an account? Login</Link>
                </div>
              </div>
            </form>
          </Grid>
        </Grid>
      )
    }
  }

export default Register
