import React, { Component } from 'react';
import { Grid, TextField, Button } from '@material-ui/core/'
import { Link } from 'react-router-dom';
import axios from 'axios';
import logo from '../image/Arkademy-Putih.svg';
import hiring from '../image/vector-hiring.png';
import './SignIn.css';

class SignIn extends Component {
  constructor(){
    super()
    this.state = {
      username: null,
      password: null,
      role: null
    }
  }

  handleLogin(e){
    e.preventDefault()
    const url = 'http://localhost:5000/v1/user/login'
    const data = {
      username: this.state.username,
      password: this.state.password
    }
    console.log("data log ", data)
    axios.post(url, data)
    .then(res => {
      this.setState({
        message: this.state.message
      })
      localStorage.setItem('token', res.data.data.token)
      localStorage.setItem('id_user', res.data.data.id_user)
      localStorage.setItem('username', res.data.data.username)
      localStorage.setItem('role', res.data.data.role)
      console.log("localStorage ",localStorage)
      this.props.history.push("/card")
    })
    .catch(err => {
      console.log(err)
    })
  }



    render() {
      return (
        <Grid container sm={12}>
          <Grid item sm={7}>
            <div className="left-side">
              <img src={logo} className="logo-white" alt="logo"></img>
              <img src={hiring} className="logo-hiring" alt="hiring"></img>
              <div className="wrap-caption">
                <h2><b>Hire expert freelancers for any jobs, online</b></h2>
                <p>Millions of small businesses use Frelancer to turn their ideas into reality.</p>
              </div>
            </div>
          </Grid>
          <Grid item sm={5}>
            <h1>Login</h1>
            <div className="login-group">
              <div className="field-username">
                <label>Username</label>
                <TextField fullWidth id="username" label="Enter your username.."
                onChange={(e) => {this.state({username: e.target.value})}} />
              </div>
              <div className="field-password">
                <label>Password</label>
                <TextField fullWidth id="password" label="Enter your password.." 
                onChange={(e) => {this.state({password: e.target.value})}}/>
              </div>
              <div className="field-role">
                <label>Role</label>
                <TextField fullWidth id="role" label="company / engineer" 
                onChange={(e) => {this.state({role: e.target.value})}}/>
                <Link className="forgot-password">Forgot Password?</Link>
              </div>
              {/* <div className="forgot-password"> */}

              {/* </div> */}
              {/* <div className="button-login"> */}
                {/* <Link to="/card" className="button-login"> */}
                  <Button variant="contained" color="primary"
                  className="button-login">
                    Login
                  </Button>
                {/* </Link> */}
              {/* </div> */}
              {/* <div className="button-register"> */}
                <Link to="/register" className="button-register">
                  <Button variant="contained" color="second">
                    Register
                  </Button>
                </Link>
              {/* </div> */}
            </div>
          </Grid>
        </Grid>
      )
    }
  }

export default SignIn
