import React, { Component, Fragment } from 'react';
import { Grid } from '@material-ui/core/';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Swal from 'sweetalert2';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider'
import AppBar from 'material-ui/AppBar'
import TextField from 'material-ui/TextField'
import RaisedButton from 'material-ui/RaisedButton'
import logo from '../image/Arkademy-Putih.svg';
import hiring from '../image/vector-hiring.png';
import { List, ListItem } from 'material-ui/List';
import '../styles/Register.css';


class Register extends Component {

    continue = e => {
      e.preventDefault();
      // PROCESS FORM ATAU SEND DATA TO API (AXIOS HERE)
      this.props.nextStep()
    }

    back = e => {
      e.preventDefault();
      this.props.prevStep()
    }

    render() {
      const { values: { username, password, role, name_engineer, description, location, birth   } } = this.props;
      return (
        <MuiThemeProvider>
                <Fragment>
                  <AppBar title="Confirm User Data" />
                  <List>
                    <ListItem
                      primaryText="Username"
                      secondaryText={ username }
                    />
                     <ListItem
                      primaryText="Password"
                      secondaryText={ password }
                    />
                     <ListItem
                      primaryText="Role user"
                      secondaryText={ role }
                    />
                     <ListItem
                      primaryText="Name"
                      secondaryText={ name_engineer }
                    />
                     <ListItem
                      primaryText="Description"
                      secondaryText={ description }
                    />
                     <ListItem
                      primaryText="Location"
                      secondaryText={ location }
                    />
                     <ListItem
                      primaryText="Date of Birth"
                      secondaryText={ birth }
                    />
                  </List>
                  <br/>
                  <RaisedButton
                    label="Confirm & Submit"
                    primary={true}
                    style={styles.button}
                    onClick={this.continue}
                  />
                  <RaisedButton
                    label="Back"
                    primary={false}
                    style={styles.button}
                    onClick={this.back}
                  />



                </Fragment>
            </MuiThemeProvider>

      )
    }
}

  const styles = {
    button: {
      margin: 15
    }
  }

export default Register
