import React, { Component, Fragment } from 'react';
import { Grid } from '@material-ui/core/';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import TypoGraphy from '@material-ui/core/Typography';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import chat from '../image/chat.png';
import bell from '../image/bell.png';
import logo from '../image/arkademy-logo.png';
import axios from 'axios';
import getAuth from '../helpers/auth';
import Cards from './Cards';
import '../styles/CardList.css';

import '../styles/Navbar.css';


class Navbar extends Component {
  constructor(props) {
    super(props)
    this.state = {
        data: [],
        search: '',
        message:'',
        loading: false,
        totalResults: 0,
        totalPages: 0,
        currentPageNo: 0
    }
    this.cancel = '';
  }

  getPagesCount = (total, denominator) => {
    const divisible = total % denominator === 0;
    const valueToBeAdded = divisible ? 0 : 1;
    return Math.floor(total / denominator) + valueToBeAdded;

    axios.get('http://localhost:5000/v1/engineer', {cancelToken: this.cancel.token,
    })
    .then((res) => {
      const total = res.data.total;
      const totalPagesCount = this.getPagesCount(total, 20);
      const resultNotFoundMsg = !res.data.hits.length
        ? 'There are no more search result. Try a new result.'
        : '';

        this.setState({
          results: res.data.hits,
          totalResults: res.data.total,
          currentPageNo: updatedPageNo,
          totalpages: totalPagesCount,
          message: resultNotFoundMsg,
          loading: false
        })
    })
  };



  handleSearch = (e) => {
    console.log("VALUES ", e.target.value)
    this.setState({search: e.target.value})
  }

  componentDidMount(){
      const auth = getAuth();
      console.log("Get Aouth ",auth)
      console.log("Get Aouth Role",auth.role)
      const authRole = auth.role
      if (authRole === 'company') {
          axios.get('http://localhost:5000/v1/engineer', { headers: { Authorization: `Bearer ${auth.token}`}})
          .then(res => {
              console.log("res axios ",res.data)
              this.setState({
                  data: res.data.result 
              })
          })
      } else if (authRole === 'engineer') {
          axios.get('http://localhost:5000/v1/engineer/profile', { headers: { Authorization: `Bearer ${auth.token}`}})
          .then(res => {
              console.log("res axios ke-2 ", res.data.data)
              this.setState({
                  data:res.data.data
              })
          })
          .catch(err => {
              console.log(err)
          })
      }
  }




    render() {
      console.log("CONSOLE ",this.state.engineer)
      let filtered = this.state.data.filter(
        (data) => {
          return data.name_engineer.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1;
        }
      )

      return (
        <Fragment>
          <AppBar position="fixed" color="inherit">
            <Toolbar>
              <div className="nav-group">
                <img src={logo} className="logo" alt="logo" />
                <div className="search">
                  <SearchIcon />
                  <InputBase
                    type="text"
                    placeholder="Searchâ€¦"
                     value={this.state.search}
              onChange={this.handleSearch.bind()}
                    inputProps={{ 'aria-label': 'search' }}
                  />
                </div>
                <div className="home">
                  <TypoGraphy variant="inherit" gutterbottom="true">
                    <h4>Home</h4>
                  </TypoGraphy>
                  <TypoGraphy className="user-detail" variant="inherit" gutterbottom="true">
                      <Avatar>U</Avatar>
                      <h4>User</h4>
                  </TypoGraphy>
                  <TypoGraphy className="typo-wrap" variant="inherit" gutterbottom="true">
                      <hr width="1" size="40" />
                      <img src={chat} className="chat" alt="chat"></img>
                      <img src={bell} className="notif" alt="bell notif"></img>
                  </TypoGraphy>
                  <Button onClick={(e) => {localStorage.removeItem('token')}}
                  >Logout</Button>
                </div>
              </div>
            </Toolbar>
          </AppBar>

          {/* START */}
          {/* <CardList /> */}
          <Grid className="map">
            {
                filtered && filtered.map(data => {
                    return <Cards key={data.id} name={data.name_engineer}
                    desc={data.description} skill={data.skill}/>
                })
            }

            <div>
              <userIdComponent name="1" onClick={this.btnClick} />
              <userIdComponent name="2" onClick={this.btnClick} />
              <userIdComponent name="3" onClick={this.btnClick} />
              <userIdComponent name="4" onClick={this.btnClick} />
            </div>
            </Grid>



        </Fragment>
      )
    }
  }

  const userIdComponent = (props) => {
    return(
      <button className="the-btn" value={props.name} onClick={props.onClick} >{props.name}</button>
    )
  }

export default Navbar
