import React, { Component, Fragment } from 'react';
import { Grid } from '@material-ui/core/';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Swal from 'sweetalert2';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider'
import AppBar from 'material-ui/AppBar'
import TextField from 'material-ui/TextField'
import RaisedButton from 'material-ui/RaisedButton'
import logo from '../image/Arkademy-Putih.svg';
import hiring from '../image/vector-hiring.png';
import { List, ListItem } from 'material-ui/List';
import '../styles/Register.css';


class Register extends Component {
    continue = e => {
      e.preventDefault();
      // PROCESS FORM ATAU SEND DATA TO API (AXIOS HERE)
      this.props.nextStep()
    }

    render() {
      const { values: { username, password, role, name_engineer, description, location, birth   } } = this.props;
      return (
        <MuiThemeProvider>
                <Fragment>
                  <AppBar title="Confirm User Data" />
                  <List>
                    <ListItem
                      primaryText="Username"
                      secondaryText={ username }
                    >

                    </ListItem>
                  </List>
                  <TextField
                    hintText="Enter your Username"
                    floatingLabelText="Username"
                    onChange={handleChange('name_engineer')}
                    defaultValue={values.username} 
                  />
                  <br/>
                  <TextField
                    hintText="Enter your password"
                    floatingLabelText="Password"
                    onChange={handleChange('password')}
                    defaultValue={values.password} 
                  />
                  <br/>
                  <TextField
                    hintText="Enter Your Role User"
                    floatingLabelText="Role User"
                    onChange={handleChange('role')}
                    defaultValue={values.role}  
                  />
                  <br/>
                  <RaisedButton
                    label="Submit"
                    primary={true}
                    style={styles.button}
                    onClick={this.continue}
                  />



                </Fragment>
            </MuiThemeProvider>

      )
    }
}

  const styles = {
    button: {
      margin: 15
    }
  }

export default Register
