import React, { Component, Fragment } from 'react';
import { Grid } from '@material-ui/core/';
import axios from 'axios';
import getAuth from '../helpers/auth';
import Cards from './Cards';
import '../styles/CardList.css';

class CardList extends Component {
    constructor() {
        super()
        this.state = {
            data: []
        }
    }

    componentDidMount(){
        const auth = getAuth();
        console.log("Get Aouth ",auth)
        console.log("Get Aouth Role",auth.role)
        axios.get('http://localhost:5000/v1/engineer')
        .then(res => {
            console.log("res axios ",res.data)
            this.setState({
                data: res.data.result 
            })
        })
    }

    render() {
        return (
            <Fragment>
                {/* <Grid container sm={12}> */}
                <Grid className="map">
                {
                    this.state.data.map(data => {
                        return <Cards key={data.id} name={data.name_engineer}
                        desc={data.description} skill={data.skill}/>
                    })
                }
                </Grid>
                {/* <Grid>

                </Grid> */}
                {/* </Grid> */}
            </Fragment>
        )
    }
}

export default CardList
