import React, { Component, Fragment } from 'react'
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider'
import AppBar from 'material-ui/AppBar'
import TextField from 'material-ui/TextField'
import RaisedButton from 'material-ui/RaisedButton'
import axios from 'axios'
import Swal from 'sweetalert2'



export class DetailEngineer extends Component {

	getFetch = ({ values: { name_engineer, description, location, birth   } }) => {
      const url = 'http://localhost:5000/v1/engineer'
      axios.post(url, { values: { name_engineer, description, location, birth   } })
      .then( res => {
        console.log("res axios ", res.data)
        // this.setState({
        //   message: res.data.message
          const success = res.data.success

          if (success === true) {
            Swal.fire({title: 'Success.',
            text: 'Your account has been created.',
            icon: 'success'})
          } else if (success === false) {
            Swal.fire({title: 'Failed.',
            text: 'This account already exist.',
            icon: 'warning'})
          }
        // })
      })
      .catch(err => {
        console.log(err)
        this.setState({
          err: err,
          message: 'Register failed.'
        })
      })
    }

     continue = e => {
      e.preventDefault();
      // PROCESS FORM ATAU SEND DATA TO API (AXIOS HERE)
      const { values: { name_engineer, description, location, birth   } } = this.props;

      this.getFetch({ values: { name_engineer, description, location, birth   } })
      this.props.nextStep()
    }


	back = e => {
		e.preventDefault();
		this.props.prevStep()
	}

    render() {
    	const { values, handleChange } = this.props;
        return (

            <MuiThemeProvider>
                <Fragment>
                	<AppBar title="Enter Personal Detail" />
                	<TextField
                		hintText="Enter your full name"
                		floatingLabelText="Name"
                		onChange={handleChange('name_engineer')}
                		defaultValue={values.name_engineer}	
                	/>
                	<br/>
                	<TextField
                		hintText="Enter your description"
                		floatingLabelText="Description"
                		onChange={handleChange('description')}
                		defaultValue={values.description}	
                	/>
                	<br/>
                	<TextField
                		hintText="Enter your location"
                		floatingLabelText="Location"
                		onChange={handleChange('location')}
                		defaultValue={values.location}	
                	/>
                	<br/>
                	<TextField
                		id="date"
                		type="date"
                		label="Date of Birth"
       					//floatingLabelText="Date of birth"
       					className="birth"
       					InputLabelProps={{
				          shrink: true,
				        }}
                		onChange={handleChange('birth')}
                		defaultValue={values.birth}	
                	/>
                	<br/>
                	<RaisedButton
                		label="Submit"
                		primary={true}
                		style={styles.button}
                		onClick={this.continue}
                	/>
                	<RaisedButton
                		label="Back"
                		primary={false}
                		style={styles.button}
                		onClick={this.back}
                	/>



                </Fragment>
            </MuiThemeProvider>
        )
    }
}

const styles = {
	button: {
		margin: 15
	}
}


export default DetailEngineer
