import React, { Component, Fragment } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import TypoGraphy from '@material-ui/core/Typography';
import Avatar from '@material-ui/core/Avatar';
import SmsRoundedIcon from '@material-ui/icons/SmsRounded';
import NotificationsIcon from '@material-ui/icons/Notifications';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import logo from '../image/arkademy-logo.png';

import './Navbar.css';


class Navbar extends Component {
    render() {
      return (
        <Fragment>
          <AppBar position="static" color="second">
            <Toolbar>
              <div className="nav-group">
                <img src={logo} className="logo" alt="logo" />
                <div className="search">
                  {/* <div className="{classes.searchIcon"> */}
                  <SearchIcon />
                  {/* </div> */}
                  <InputBase
                    placeholder="Search…"
                    // classes={{
                    //   root: classes.inputRoot,
                    //   input: classes.inputInput,
                    // }}
                    inputProps={{ 'aria-label': 'search' }}
                  />
                </div>
                <div className="home">
                  <TypoGraphy variant="title" gutterbottom>
                    <h4>Home</h4>
                  </TypoGraphy>
                  <TypoGraphy className="user-detail" variant="title" gutterbottom>
                      <Avatar>U</Avatar>
                      <h4>User</h4>
                  </TypoGraphy>
                  <TypoGraphy className="typo-wrap" variant="title" gutterbottom>
                      <hr width="1" size="40" />
                      <SmsRoundedIcon className="chat"/>
                      <NotificationsIcon className="notif"/>
                  </TypoGraphy>
                </div>
              </div>
            </Toolbar>
          </AppBar>
          <Grid container sm={12} className="grid-container">
          <Grid container spacing={3} className="display">
        <Grid item xs={12}>
          <Paper className="{classes.paper}">xs=12</Paper>
        </Grid>
        <Grid item xs={6}>
          <Paper className="{classes.paper}">xs=6</Paper>
        </Grid>
        <Grid item xs={6}>
          <Paper className="{classes.paper}">xs=6</Paper>
        </Grid>
        <Grid item xs={3}>
          <Paper className="{classes.paper}">xs=3</Paper>
        </Grid>
        <Grid item xs={3}>
          <Paper className="{classes.paper}">xs=3</Paper>
        </Grid>
        <Grid item xs={3}>
          <Paper className="{classes.paper}">xs=3</Paper>
        </Grid>
        <Grid item xs={3}>
          <Paper className="{classes.paper}">xs=3</Paper>
        </Grid>
      </Grid>
        </Grid>
        </Fragment>
      )
    }
  }

export default Navbar


